import React, { useState } from "react";
import ROLE from "../common/role";
import { IoMdClose } from "react-icons/io";

const ChangeUserRole = () => ({
    name,
    email,
    role,
})=>{
    const [userRole, setUserRole] = useState(role)
    const handleOnChangeSelect = (e) => {
        setUserRole(e.target.value)
        console.log(e.target.value);
    }
    return (
    
        <div className="'fixed top-0 bottom-0 left-0 right-0 w-full h-full z-10 flex justify-between items-center bg-red-500">
            <div className=" mx-auto bg-white shadow-md p-4 w-full max-w-sm">
                <button className="block ml-auto">
                    <IoMdClose/>
                </button>

                <h1 className="pb-4 text-lg font-medium"> ChangeUserRole</h1>
                <p>name:{name}</p>
                <p>email:{email}</p>
                <div className="flex items-center justify-between my-4">
                    <p>Role:</p>
                <select className="border px-4 py-1" value={userRole} onChange={handleOnChangeSelect}>
                    {
                        Object.values(ROLE).map(el => {
                            return (
                                <option  value={el} key={el}>{el}</option>
                            )
                        })
                    }
                    
                </select>
                </div>
                <button className="w-fit mx-auto block  p-2 rounded-full bg-red-600 text-white hover:bg-red-700">Change Role</button>

            </div>
        </div>
    )
};

export default ChangeUserRole;
