import React, { useEffect, useState } from "react";
import fetchCategoryWiseProduct from "../helpers/fetchCategoryWiseProduct";

const HorizontalCardProduct = ({ category, heading }) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const categoryProduct = await fetchCategoryWiseProduct(category);
                setData(categoryProduct?.data);
            } catch (error) {
                console.error('Error fetching category-wise product:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [category]); // Include category in the dependency array if fetchData depends on it

    return (
        <div className="container mx-auto pz-4 my-6">
            <h2 className="text-2xl font-semibold py-4">{heading}</h2>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {data.map((product, index) => (
                        <div key={index} className="bg-white rounded-sm shadow">
                            <div className="p-4">
                                <img src={product.productImage[0]} alt="Product Image" className="w-full h-auto" />
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default HorizontalCardProduct;
