import React, { useState } from 'react';
import loginIcons from '../assets/signin.gif';
import { FaEye, FaEyeSlash } from "react-icons/fa";
import { Link, useNavigate } from 'react-router-dom';
import imageTobase64 from '../helpers/imageTobase64';
import SummaryApi from '../common';
import { toast } from 'react-toastify';

const SignUp = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [data, setData] = useState({
    email: "",
    password: "",
    name: "",
    confirmPassword: "",
    profilePic: "",
  });
  const navigate = useNavigate();

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });
  };

  const handleUploadPic = async (e) => {
    const file = e.target.files[0];
    const imagePic = await imageTobase64(file);
    setData((prev) => {
      return {
        ...prev,
        profilePic: imagePic,
      };
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (data.password === data.confirmPassword) {
      const dataResponse = await fetch(SummaryApi.signUP.url, {
        method: SummaryApi.signUP.method,
        headers: {
          "content-type": "application/json",
        },
        body: JSON.stringify(data),
      });

      const dataApi = await dataResponse.json();

      if (dataApi.success) {
        toast.success(dataApi.message);
        navigate("/login");
      }

      if (dataApi.error) {
        toast.error(dataApi.message);
      }
    } else {
      toast.error("Please check password and confirm password");
    }
  };

  return (
    <section id='signup' className='min-h-screen flex items-center justify-center bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500'>
      <div className='container mx-auto p-4'>
        <div className='bg-white p-5 w-full max-w-sm mx-auto rounded-lg shadow-lg'>
          <div className='w-20 h-20 mx-auto relative overflow-hidden rounded-full mb-4'>
            <img src={data.profilePic || loginIcons} alt='login icons' className='w-full h-full object-cover'/>
            <form>
              <label>
                <div className='text-xs bg-opacity-80 bg-slate-200 pb-4 pt-2 cursor-pointer text-center absolute bottom-0 w-full'>
                  Upload Photo
                </div>
                <input type='file' className='hidden' onChange={handleUploadPic}/>
              </label>
            </form>
          </div>

          <form className='pt-6 flex flex-col gap-4' onSubmit={handleSubmit}>
            <div className='grid'>
              <label className='text-sm font-medium'>Name:</label>
              <div className='bg-slate-100 p-2 rounded-md'>
                <input 
                  type='text' 
                  placeholder='Enter your name' 
                  name='name'
                  value={data.name}
                  onChange={handleOnChange}
                  required
                  className='w-full h-full outline-none bg-transparent'/>
              </div>
            </div>
            <div className='grid'>
              <label className='text-sm font-medium'>Email:</label>
              <div className='bg-slate-100 p-2 rounded-md'>
                <input 
                  type='email' 
                  placeholder='Enter email' 
                  name='email'
                  value={data.email}
                  onChange={handleOnChange}
                  required
                  className='w-full h-full outline-none bg-transparent'/>
              </div>
            </div>
            <div>
              <label className='text-sm font-medium'>Password:</label>
              <div className='bg-slate-100 p-2 flex rounded-md'>
                <input 
                  type={showPassword ? "text" : "password"} 
                  placeholder='Enter password'
                  value={data.password}
                  name='password' 
                  onChange={handleOnChange}
                  required
                  className='w-full h-full outline-none bg-transparent'/>
                <div className='cursor-pointer text-xl' onClick={() => setShowPassword((prev) => !prev)}>
                  {showPassword ? <FaEyeSlash/> : <FaEye/>}
                </div>
              </div>
            </div>
            <div>
              <label className='text-sm font-medium'>Confirm Password:</label>
              <div className='bg-slate-100 p-2 flex rounded-md'>
                <input 
                  type={showConfirmPassword ? "text" : "password"} 
                  placeholder='Enter confirm password'
                  value={data.confirmPassword}
                  name='confirmPassword' 
                  onChange={handleOnChange}
                  required
                  className='w-full h-full outline-none bg-transparent'/>
                <div className='cursor-pointer text-xl' onClick={() => setShowConfirmPassword((prev) => !prev)}>
                  {showConfirmPassword ? <FaEyeSlash/> : <FaEye/>}
                </div>
              </div>
            </div>
            <button className='bg-red-600 hover:bg-red-700 text-white px-6 py-2 w-full max-w-[150px] rounded-full hover:scale-110 transition-transform duration-200 ease-in-out mx-auto block mt-6'>Sign Up</button>
          </form>

          <p className='my-5 text-center'>Already have an account? <Link to={"/login"} className='text-red-600 hover:text-red-700 hover:underline'>Login</Link></p>
        </div>
      </div>
    </section>
  );
};

export default SignUp;
